int s ( int a , int b ) { if ( a > b ) return b ; else return a ; } int main ( void ) { int p , q , t , i , l , w ; scanf ( "%d" , & t ) ; while ( t ) { l = 1 ; scanf ( "%d%d" , & p , & q ) ; i = s ( p , q ) ; w = 0 ; while ( i > 1 ) { if ( p % i == 0 && q % i == 0 ) { printf ( "%d" , i ) ; w = 1 ; p = p / i ; q = q / i ; l = l * i ; } else i -- ; } if ( i == 1 && w == 0 ) printf ( "%d" , i ) ; printf ( " %d\n" , l * p * q ) ; t -- ; } return 0 ; }