int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i ; scanf ( "%lld" , & n ) ; long long int a [ n ] , b [ n ] , count = 0 ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { b [ i ] = 1 ; } for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] > a [ i - 1 ] ) b [ i ] = b [ i ] + b [ i - 1 ] ; } for ( i = 0 ; i < n ; i ++ ) { count = count + b [ i ] ; } printf ( "%lld\n" , count ) ; } return 0 ; }