int main ( ) { int t , n , i ; scanf ( "%d" , & t ) ; while ( t -- ) { int c ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } c = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( c > a [ i ] ) { c = a [ i ] ; } } printf ( "%d\n" , c * ( n - 1 ) ) ; } }