int max_sum ; short int rows , arr [ 99 ] [ 99 ] ; void path_enum ( short int cur_row , short int cur_col , int sum ) { sum += arr [ cur_row ] [ cur_col ] ; if ( cur_row == rows ) { if ( sum > max_sum ) max_sum = sum ; } else { path_enum ( cur_row + 1 , cur_col , sum ) ; path_enum ( cur_row + 1 , cur_col + 1 , sum ) ; } } main ( ) { int no ; short int count = 0 , i , j ; scanf ( "%d" , & no ) ; int * results = ( int ) malloc ( 2 * no ) ; while ( no ) { scanf ( "%d" , & rows ) ; max_sum = 0 ; for ( i = 0 ; i < rows ; ++ i ) { for ( j = 0 ; j <= i ; ++ j ) scanf ( "%d" , & arr [ i ] [ j ] ) ; } path_enum ( 0 , 0 , 0 ) ; results [ count ++ ] = max_sum ; -- no ; } for ( i = 0 ; i < count ; ++ i ) printf ( "%d\n" , results [ i ] ) ; }