int greater ( int x , int y ) { if ( x > y ) return x ; else return y ; } int find_num ( int a [ 100 ] [ 100 ] , int p1 , int p2 , int r1 , int r2 ) { if ( p1 == r1 && p2 == r2 ) return ( a [ p1 ] [ p2 ] ) ; else return ( a [ p1 ] [ p2 ] + greater ( find_num ( a , p1 + 1 , p2 , r1 , r2 - 1 ) , find_num ( a , p1 + 1 , p2 + 1 , r1 , r2 ) ) ) ; } int main ( ) { int t ; int a [ 100 ] [ 100 ] , i , j , n , k ; scanf ( "%d" , & t ) ; for ( k = 0 ; k < t ; k ++ ) { scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } printf ( "%d\n" , find_num ( a , 0 , 0 , n , n ) ) ; } return 0 ; }