long int findhcf ( long int A , long int B ) { if ( B == 0 ) { return A ; } else { return findhcf ( B , A % B ) ; } } long int findlcm ( long int A , long int B , long int y ) { long int x ; x = findhcf ( A , B ) ; if ( x == 1 ) { return A * B * y ; } else { y = y * x ; return findlcm ( A / x , B / x , y ) ; } } int main ( ) { int T , i ; long int A , B , gcd , lcm , y ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { y = 1 ; scanf ( "%ld %ld" , & A , & B ) ; gcd = findhcf ( A , B ) ; lcm = findlcm ( A , B , y ) ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }