int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( long long int i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } int s = a [ 0 ] ; long long int sum = 0 ; for ( long long int i = 1 ; i < n ; i ++ ) { if ( s > a [ i ] ) { s = a [ i ] ; } } sum = s * ( n - 1 ) ; printf ( "%lld\n" , sum ) ; } return 0 ; }