int main ( ) { int t ; long int k , a , b , j , i ; scanf ( "%d" , & t ) ; { while ( t -- ) { k = 1 ; scanf ( "%ld%ld" , & a , & b ) ; i = b ; while ( ( a % i != 0 ) && ( b % i != 0 ) ) i -- ; printf ( "%ld\t" , i ) ; j = 2 ; while ( j < b ) { while ( ( a % j == 0 ) && ( b % j == 0 ) ) { k = k * j ; a = a / j ; b = b / j ; } j ++ ; } printf ( "%ld\n" , k * a * b ) ; } } return 0 ; }