int main ( ) { long int t , n , i , a [ 100000 ] , sum = 0 ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { sum += a [ i + 1 ] ; } else { sum += a [ i ] ; } } printf ( "%ld\n" , sum ) ; sum = 0 ; } return 0 ; }