int main ( ) { int n , i , j , r , sum ; scanf ( "%d" , & r ) ; for ( j = 0 ; j < r ; j ++ ) { scanf ( "%d" , & n ) ; int a [ 2 ] ; scanf ( "%d %d" , & a [ 0 ] , & a [ 1 ] ) ; if ( a [ 0 ] > a [ 1 ] ) a [ 0 ] = a [ 1 ] ; sum = a [ 0 ] ; for ( i = 0 ; i < n - 2 ; i ++ ) { scanf ( "%d" , & a [ 1 ] ) ; if ( a [ 0 ] > a [ 1 ] ) a [ 0 ] = a [ 1 ] ; sum = sum + a [ 0 ] ; } printf ( "%d\n" , sum ) ; } }