int main ( ) { long long int t , n , i ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld" , & n ) ; int a [ n ] , min ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < min ) { min = min + a [ i ] ; a [ i ] = min - a [ i ] ; min = min - a [ i ] ; } } printf ( "%lld\n" , min * ( n - 1 ) ) ; } return 0 ; }