main ( ) { int t ; long int i , j , k , l ; long int A [ 1000 ] ; long int B [ 1000 ] ; long int gcd [ 1000 ] ; long int lcm [ 1000 ] ; scanf ( "%d" , & t ) ; for ( j = 0 ; j < t ; j ++ ) { int n = 0 , m = 0 ; scanf ( "%ld %ld" , & A [ j ] , & B [ j ] ) ; for ( i = 1 ; i <= A [ j ] && i <= B [ j ] ; i ++ ) { if ( A [ j ] % i == 0 && B [ j ] % i == 0 ) { gcd [ j ] = i ; } } lcm [ j ] = A [ j ] * B [ j ] / gcd [ j ] ; } for ( k = 0 ; k < t ; k ++ ) { printf ( "%ld %ld\n" , gcd [ k ] , lcm [ k ] ) ; } }