int main ( ) { int n , lines , i , j , k = 0 ; scanf ( "%d" , & n ) ; while ( k ++ < n ) { scanf ( "%d" , & lines ) ; int * * arr = ( int * * ) malloc ( lines * sizeof ( int * ) ) ; for ( i = 0 ; i < lines ; i ++ ) { arr [ i ] = ( int * ) malloc ( ( i + 1 ) * sizeof ( int ) ) ; } for ( i = 0 ; i < lines ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr [ i ] [ j ] ) ; } } for ( i = lines - 2 ; i >= 0 ; i -- ) { for ( j = 0 ; j <= i ; j ++ ) { if ( arr [ i + 1 ] [ j ] > arr [ i + 1 ] [ j + 1 ] ) { arr [ i ] [ j ] += arr [ i + 1 ] [ j ] ; } else { arr [ i ] [ j ] += arr [ i + 1 ] [ j + 1 ] ; } } } printf ( "%d" , arr [ 0 ] [ 0 ] ) ; for ( i = 0 ; i < lines ; i ++ ) { free ( arr [ i ] ) ; } free ( arr ) ; if ( k != n ) printf ( "\n" ) ; } return 0 ; }