int getGCD ( int a , int b ) { long int diff ; long int m , n ; if ( a > b ) { m = a ; n = b ; } else { m = b ; n = a ; } if ( m % n == 0 ) return n ; else { diff = m - n ; return getGCD ( n , diff ) ; } } int main ( ) { int t ; long int gcd , lcm ; long int a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; gcd = getGCD ( a , b ) ; lcm = ( a * b ) / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }