int main ( void ) { long long a , b , k , i , t , j , num , nam ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; j = 2 ; k = 1 ; num = a ; nam = b ; while ( a != 1 || b != 1 ) { if ( a % j == 0 || b % j == 0 ) { if ( a % j == 0 ) a = a / j ; if ( b % j == 0 ) b = b / j ; k = k * j ; } else j ++ ; } printf ( "%lld %lld\n" , ( num * nam ) / k , k ) ; } return 0 ; }