int main ( ) { unsigned short int testcase , i , distinctInt ; int min , value ; unsigned long long int cost ; scanf ( "%hu" , & testcase ) ; while ( testcase -- ) { scanf ( "%hu" , & distinctInt ) ; min = 100000 ; for ( i = 1 ; i <= distinctInt ; i ++ ) { scanf ( "%d" , & value ) ; if ( value < min ) { min = value ; } } cost = min * ( distinctInt - 1 ) ; printf ( "%llu\n" , cost ) ; } }