int main ( ) { int a [ 100 ] [ 100 ] , n , l , i , j ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & l ) ; for ( i = 0 ; i < l ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = l - 2 ; i >= 0 ; i -- ) { for ( j = 0 ; j <= i ; j ++ ) { a [ i ] [ j ] += max ( a [ i + 1 ] [ j ] , a [ i + 1 ] [ j + 1 ] ) ; } } printf ( "%d\n" , a [ 0 ] [ 0 ] ) ; } return 0 ; }