int main ( ) { int t , n1 , n2 , i , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { lcm = 1 ; scanf ( "%d %d" , & n1 , & n2 ) ; int n ; n = n1 >= n2 ? n1 : n2 ; for ( i = 1 ; i < n ; i ++ ) { if ( ( n1 % i == 0 ) && ( n2 % i == 0 ) ) { gcd = i ; } if ( ( n1 % i == 0 ) && ( n2 % i == 0 ) ) { lcm = lcm * i ; n1 = n1 / i ; n2 = n2 / i ; } else if ( ( n1 % i == 0 ) && ( n2 % i != 0 ) ) { lcm = lcm * i ; n1 = n1 / i ; } else if ( ( n1 % i != 0 ) && ( n2 % i == 0 ) ) { lcm = lcm * i ; n2 = n2 / i ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }