int main ( ) { long long int T , A , B ; scanf ( "%lld" , & T ) ; while ( T -- ) { long long int p = 0 , q = 0 , l = 0 ; scanf ( "%lld %lld" , & A , & B ) ; if ( A > B ) { p = A ; q = B ; } else { p = B ; q = A ; } l = p % q ; while ( l > 0 ) { p = q ; q = l ; l = p % q ; } long long int GCD = q ; long long int LCM = ( A * B ) / GCD ; printf ( "%lld %lld\n" , GCD , LCM ) ; } return 0 ; }