int main ( ) { int t ; scanf ( "%d" , & t ) ; int i , j , k , count ; int h [ t ] ; for ( count = 0 ; count < t ; count ++ ) { int l ; scanf ( "%d" , & l ) ; int a [ l ] [ l ] ; for ( i = 0 ; i < l ; i ++ ) for ( j = 0 ; j < l ; j ++ ) a [ i ] [ j ] = 0 ; for ( i = 1 ; i <= l ; i ++ ) { for ( j = 0 ; j < i ; j ++ ) { int m ; scanf ( "%d" , & m ) ; a [ i - 1 ] [ j ] = m ; } } int ms [ l ] ; int curr_r = 0 ; int curr_c = 0 ; ms [ 0 ] = a [ 0 ] [ 0 ] ; for ( i = 0 ; i < l - 1 ; i ++ ) { if ( a [ curr_r + 1 ] [ curr_c ] > a [ curr_r + 1 ] [ curr_c + 1 ] ) { curr_r = curr_r + 1 ; ms [ i + 1 ] = a [ curr_r ] [ curr_c ] ; } else { curr_r = curr_r + 1 ; curr_c = curr_c + 1 ; ms [ i + 1 ] = a [ curr_r ] [ curr_c ] ; } } int sum = 0 ; for ( k = 0 ; k < l ; k ++ ) sum = sum + ms [ k ] ; h [ count ] = sum ; } for ( j = 0 ; j < t ; j ++ ) printf ( "%d\n" , h [ j ] ) ; }