long long int gcd ( long long int x , long long int y ) { long long int rem ; rem = x % y ; if ( rem == 0 ) return y ; else return ( gcd ( y , rem ) ) ; } int main ( ) { long long int a , b , i , lcm , hcf ; int t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & a , & b ) ; hcf = gcd ( a , b ) ; lcm = ( a * b ) / hcf ; printf ( "%lld %lld\n" , hcf , lcm ) ; } return 0 ; }