int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n1 , n2 , n = 0 , d = 0 , gcd = 0 , lcm = 0 , r = 0 ; scanf ( "%d%d" , & n1 , & n2 ) ; if ( n1 > n2 ) { n = n1 ; d = n2 ; } else { n = n2 ; d = n1 ; } r = n1 % n2 ; while ( r != 0 ) { n = d ; d = r ; r = n % d ; } gcd = d ; lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }