int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { int n , i ; long long int sum = 0 ; scanf ( "%d" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } int d [ n ] ; d [ 0 ] = 1 ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i - 1 ] <= a [ i ] ) { d [ i ] = d [ i - 1 ] + 1 ; } else { d [ i ] = 1 ; } } for ( i = 0 ; i < n ; i ++ ) { sum = sum + d [ i ] ; } printf ( "%lld\n" , sum ) ; } return 0 ; }