int main ( ) { int cases , * * triangle , i , j , rows , k ; scanf ( "%d" , & cases ) ; for ( k = 0 ; k < cases ; k ++ ) { scanf ( "%d" , & rows ) ; triangle = ( int * * ) malloc ( sizeof ( int * ) * rows ) ; for ( i = 0 ; i < rows ; i ++ ) triangle [ i ] = ( int * ) malloc ( sizeof ( int ) * ( i + 1 ) ) ; for ( i = 0 ; i < rows ; i ++ ) for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & triangle [ i ] [ j ] ) ; for ( i = rows - 1 ; i > 0 ; i -- ) { for ( j = 0 ; j < i ; j ++ ) { if ( triangle [ i ] [ j ] > triangle [ i ] [ j + 1 ] ) triangle [ i - 1 ] [ j ] += triangle [ i ] [ j ] ; else triangle [ i - 1 ] [ j ] += triangle [ i ] [ j + 1 ] ; } } printf ( "%d\n" , * * triangle ) ; } return 0 ; }