void perform ( ) { unsigned int n , i ; scanf ( "%lu" , & n ) ; int l , a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } l = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < l ) l = a [ i ] ; } printf ( "%lu\n" , ( n - 1 ) * l ) ; } int main ( ) { int nooftestcases , i ; scanf ( "%d" , & nooftestcases ) ; for ( i = 0 ; i < nooftestcases ; i ++ ) { perform ( ) ; } return 0 ; }