int gcd ( long int a , long int b ) { if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( long int a , long int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { long int a , b , t , val , val1 ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; val = gcd ( a , b ) ; val1 = lcm ( a , b ) ; printf ( "%ld %ld\n" , val , val1 ) ; } return 0 ; }