int main ( ) { long int i , j , t , a , b , x ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) x = i ; } printf ( "%ld " , x ) ; long int y = 1 ; if ( a % 2 == 0 && b % 2 == 0 ) { while ( a != 0 && b != 0 ) { a = a / 2 ; b = b / 2 ; y = y * 2 ; } } for ( i = 3 ; i <= a && i <= b ; i = i + 2 ) { if ( a % i == 0 && b % i == 0 ) { while ( a != 0 && b != 0 ) { a = a / i ; b = b / i ; y = y * i ; } } } if ( a >= 2 ) y = y * a ; if ( b >= 2 ) y = y * b ; printf ( "%ld\n" , y ) ; } return 0 ; }