int main ( ) { int T , n , x , y ; scanf ( "%d" , & T ) ; int a [ T - 1 ] , b [ T - 1 ] , g [ T - 1 ] , l [ T - 1 ] ; for ( n = 0 ; n <= T - 1 ; n ++ ) { scanf ( "%d %d" , & a [ n ] , & b [ n ] ) ; } for ( n = 0 ; n <= T - 1 ; n ++ ) { x = ( a [ n ] > b [ n ] ) ? a [ n ] : b [ n ] ; y = ( a [ n ] < b [ n ] ) ? a [ n ] : b [ n ] ; q : if ( a [ n ] % y == 0 && b [ n ] % y == 0 ) g [ n ] = y ; else { y -- ; goto q ; } p : if ( x % a [ n ] == 0 && x % b [ n ] == 0 ) l [ n ] = x ; else { x ++ ; goto p ; } } printf ( "\n" ) ; for ( n = 0 ; n <= T - 1 ; n ++ ) printf ( "%d %d\n" , g [ n ] , l [ n ] ) ; }