int main ( ) { int n1 , n2 , i , gcd , lcm , j , n , f , a ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { f = 0 ; scanf ( "%d %d" , & n1 , & n2 ) ; a = ( n1 < n2 ) ? n1 : n2 ; for ( j = 1 ; j <= a / 2 ; j ++ ) { if ( n1 % j == 0 && n2 % j == 0 ) { gcd = j ; f = 1 ; break ; } } if ( f = 0 ) gcd = 1 ; lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }