int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a == 0 ) return b ; else if ( b == 0 ) return a ; else if ( a > b ) return gcd ( b , a % b ) ; else return gcd ( a , b % a ) ; } int main ( ) { long long int a , b ; int t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & a , & b ) ; long long int g = gcd ( a , b ) ; long long int lcm = ( a * b ) / g ; printf ( "%lld %lld\n" , g , lcm ) ; } return 0 ; }