int main ( void ) { int T = 0 , i = 0 ; int a = 0 , b = 0 , n = 0 , count = 1 ; long int ans = 0 ; scanf ( "%d" , & T ) ; while ( T -- ) { count = 1 ; a = 0 ; b = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { if ( i == 0 ) { scanf ( "%d" , & a ) ; count = 1 ; ans += count ; } else { scanf ( "%d" , & b ) ; if ( a <= b ) { count ++ ; } else { count = 1 ; } ans += count ; a = b ; } } printf ( "%ld" , ans ) ; ans = 0 ; } }