int main ( ) { int f , n , i , j , k , p , big , q = 0 ; int A [ 20 ] [ 20 ] ; int sum ; scanf ( "%d" , & f ) ; while ( q < f ) { scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) scanf ( "%d" , & A [ i ] [ j ] ) ; for ( j = i + 1 ; j <= n ; j ++ ) A [ i ] [ j ] = 0 ; } big = A [ n ] [ 1 ] ; for ( k = 2 ; k <= n ; k ++ ) { if ( big < A [ n ] [ k ] ) { big = A [ n ] [ k ] ; p = k ; } } sum = big ; while ( n > 1 ) { if ( p > 1 ) { if ( A [ n - 1 ] [ p - 1 ] >= A [ n - 1 ] [ p ] ) { sum = sum + A [ n - 1 ] [ p - 1 ] ; p -- ; } else sum = sum + A [ n - 1 ] [ p ] ; } else sum = sum + A [ n - 1 ] [ p ] ; n -- ; } q ++ ; printf ( "%d" , sum ) ; } }