int gcd ( int a , int b ) { if ( a == 0 ) return a ; if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , lcm ; scanf ( "%d%d" , & a , & b ) ; int k = gcd ( a , b ) ; printf ( "%d" , k ) ; lcm = ( a > b ) ? a : b ; while ( 1 ) { if ( lcm % a == 0 && lcm % b == 0 ) { printf ( " %d\n" , lcm ) ; break ; } ++ lcm ; } } return 0 ; }