int a [ 100 ] [ 100 ] ; int main ( ) { int i , j , h ; int sum ; int n ; scanf ( "%d" , & n ) ; while ( n -- ) { sum = 0 ; scanf ( "%d" , & h ) ; for ( i = 0 ; i < h ; i ++ ) for ( j = 0 ; j < h ; j ++ ) if ( j <= i ) scanf ( "%d" , & a [ i ] [ j ] ) ; printf ( "%d\n" , dfs_sum ( 0 , 0 , h ) ) ; } return 0 ; } int dfs_sum ( int x , int y , int h ) { if ( y == h - 1 ) return a [ x ] [ y ] ; else { if ( dfs_sum ( x + 1 , y + 1 , h ) > dfs_sum ( x , y + 1 , h ) ) return a [ x ] [ y ] + dfs_sum ( x + 1 , y + 1 , h ) ; else return a [ x ] [ y ] + dfs_sum ( x , y + 1 , h ) ; } }