int max ( int a , int b ) ; int min ( int a , int b ) ; int main ( ) { long int T ; int i ; scanf ( "%ld" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { long int A , B ; long int j = 0 , t = 0 , g = 0 ; scanf ( "%ld%ld" , & A , & B ) ; if ( max ( A , B ) % min ( A , B ) == 0 ) g = min ( A , B ) ; else { for ( j = min ( A , B ) ; j >= 1 ; j -- ) { if ( max ( A , B ) % j == 0 && min ( A , B ) % j == 0 ) { g = j ; break ; } } } t = ( A * B ) / g ; printf ( "%ld %ld\n" , g , t ) ; } return 0 ; } int max ( int a , int b ) { return ( a > b ? a : b ) ; } int min ( int a , int b ) { return ( a < b ? a : b ) ; }