int main ( void ) { int grt , n , m , k , i , j ; scanf ( "%d" , & n ) ; if ( n > 1000 ) return 0 ; while ( n != 0 ) { scanf ( "%d" , & m ) ; if ( m != 0 ) { int a [ m ] [ m ] ; int b [ m + 1 ] [ 2 * m ] ; for ( i = 0 ; i < m + 1 ; i ++ ) { for ( j = 0 ; j < 2 * m ; j ++ ) { if ( i <= m - 1 && j <= m - 1 ) a [ i ] [ j ] = 0 ; b [ i ] [ j ] = 0 ; } } for ( i = 0 ; i < m ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } b [ 0 ] [ 0 ] = a [ 0 ] [ 0 ] ; for ( i = 0 ; i < m - 1 ; i ++ ) { k = 0 ; for ( j = 0 ; j <= i ; j ++ ) { b [ i + 1 ] [ k ++ ] = b [ i ] [ j ] + a [ i + 1 ] [ j ] ; b [ i + 1 ] [ k ++ ] = b [ i ] [ j ] + a [ i + 1 ] [ j + 1 ] ; } } grt = b [ m - 1 ] [ 0 ] ; for ( j = 0 ; j < 2 * m ; j ++ ) { if ( grt < b [ m - 1 ] [ j ] ) grt = b [ m - 1 ] [ j ] ; } if ( n == 1 ) printf ( "%d" , grt ) ; else printf ( "%d\n" , grt ) ; n -- ; } } return 0 ; }