int main ( ) { int t , i ; unsigned long long int gcd , lcm , a , b , small , big ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%llu" , & a ) ; scanf ( "%llu" , & b ) ; if ( a > b ) { small = b ; big = a ; } else { small = a ; big = b ; } if ( a % small == 0 && b % small == 0 ) { gcd = small ; } else { gcd = ( small + 1 ) / 2 ; while ( a % gcd != 0 || b % gcd != 0 ) { gcd = gcd - 1 ; } } if ( gcd == small ) { lcm = big ; } else { lcm = ( a * b ) / gcd ; } printf ( "%llu %llu\n" , gcd , lcm ) ; } return 0 ; }