int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b , min , i , j , k = 0 , lcm = 1 , hcf ; scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) min = b ; else min = a ; for ( j = min ; j > 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { lcm = lcm * j ; a = a / j ; b = b / j ; if ( k == 0 ) hcf = j ; k ++ ; } } lcm = lcm * a * b ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; }