int gcd ( long long int a , long long int b ) { long long int r ; r = a % b ; if ( r == 0 ) { return b ; } return ( gcd ( b , r ) ) ; } int main ( void ) { long long int t ; scanf ( "%lld" , & t ) ; long long int a , b ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; long long int g = gcd ( a , b ) ; long long int l = ( a * b ) / g ; printf ( "%lld %lld\n" , g , l ) ; } return 0 ; }