void gcd ( int c , int d ) { int a , b , r , temp ; a = ( c > d ) ? c : d ; b = ( c < d ) ? c : d ; r = ( a % b ) ; if ( r == 0 ) printf ( "%d " , b ) ; else { while ( r != 0 ) { temp = r ; r = ( b % r ) ; b = temp ; } printf ( "%d " , b ) ; } return ; } void lcm ( int c , int d ) { int a , b , i , k ; a = ( c > d ) ? c : d ; b = ( c < d ) ? c : d ; for ( i = 1 ; i <= b ; i ++ ) { k = a * i ; if ( k % b == 0 ) { printf ( "%d\n" , k ) ; break ; } } return ; } int main ( void ) { int c , d , t ; scanf ( "%d" , & t ) ; while ( t ) { scanf ( "%d%d" , & c , & d ) ; gcd ( c , d ) ; lcm ( c , d ) ; t -- ; } return 0 ; }