int lcm ( int , int ) ; int gcd ( int , int ) ; int main ( ) { int T ; long long int A , B , x , y ; scanf ( "%lld" , & T ) ; while ( T -- ) { scanf ( "%lld %lld" , & A , & B ) ; x = gcd ( A , B ) ; y = ( A * B ) / x ; printf ( "%lld %lld\n" , x , y ) ; } return 0 ; } int gcd ( int m , int n ) { long long int i , gcd ; for ( i = 1 ; i <= m && i <= n ; ++ i ) { if ( m % i == 0 && n % i == 0 ) { gcd = i ; } } return ( gcd ) ; }