int main ( ) { int n , T , G , L , A , B , X ; scanf ( "%d" , & T ) ; for ( n = 1 ; n <= T ; n ++ ) { if ( T < 1000 ) { scanf ( "%d %d" , & A , & B ) ; if ( A <= 1000000 && B <= 1000000 ) { if ( A < B ) { X = A ; } else { X = B ; } for ( ; X >= 1 ; X -- ) { if ( A % X == 0 && B % X == 0 ) { G = X ; L = ( A * B ) / G ; break ; } } printf ( "%d %d\n" , G , L ) ; } break ; } } return 0 ; }