int main ( ) { int i , a ; scanf ( "%d" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { int num1 , num2 , gcd , lcm , remainder , numerator , denominator ; scanf ( "%d %d" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = num1 % num2 ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = num1 * num2 / gcd ; printf ( "%d " , gcd ) ; printf ( "%d \n" , lcm ) ; } return 0 ; }