int main ( ) { int t , n , p , c , i ; long long total , r ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; scanf ( "%d" , & p ) ; r = 1 ; total = 0 ; for ( i = 1 ; i < n ; i ++ ) { scanf ( "%d" , & c ) ; if ( p <= c ) { r ++ ; } else { r = r * ( r + 1 ) / 2 ; total = total + r ; r = 1 ; } p = c ; } r = r * ( r + 1 ) / 2 ; total = total + r ; printf ( "%lld\n" , total ) ; } return 0 ; }