long long int gcd ( long long int , long long int ) ; long long int lcm ( long long int , long long int ) ; int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { long long int a , b ; scanf ( "%lld%lld" , & a , & b ) ; long long int g = gcd ( a , b ) ; long long int l = lcm ( a , b ) ; printf ( "%lld %lld\n" , g , l ) ; } return 0 ; } long long int gcd ( long long int a , long long int b ) { if ( b == 0 ) return a ; else if ( a == 0 ) return b ; else return gcd ( b , a % b ) ; } long long int lcm ( long long int a , long long int b ) { long long int l ; l = ( a * b ) / gcd ( a , b ) ; return l ; }