int main ( ) { int i , j ; scanf ( "%d" , & i ) ; for ( j = 0 ; j < i ; j ++ ) { int k , n , p , l ; int * v ; scanf ( "%d" , & k ) ; n = k * ( k + 1 ) / 2 ; v = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( l = 0 ; l < n ; l ++ ) { scanf ( "%d" , & v [ l ] ) ; } for ( p = k - 1 ; p > 0 ; p -- ) { int z ; for ( z = 0 ; z < p ; z ++ ) { int x , y ; x = v [ ( p * ( p - 1 ) / 2 ) + z ] + v [ ( p * ( p - 1 ) / 2 ) + z + p ] ; y = v [ ( p * ( p - 1 ) / 2 ) + z ] + v [ ( p * ( p - 1 ) / 2 ) + z + p + 1 ] ; if ( x > y ) { v [ ( p * ( p - 1 ) / 2 ) + z ] = x ; } else { v [ ( p * ( p - 1 ) / 2 ) + z ] = y ; } } } printf ( "%d\n" , v [ 0 ] ) ; free ( v ) ; } return 0 ; }