int main ( ) { long long int t , i ; scanf ( "%lld" , & t ) ; while ( t > 0 ) { long long int n , i , j , c = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } long long int dp [ n ] ; for ( i = 0 ; i < n ; i ++ ) { dp [ i ] = 1 ; } for ( i = n - 2 ; i >= 0 ; i -- ) { if ( a [ i ] <= a [ i + 1 ] ) dp [ i ] = dp [ i ] + dp [ i + 1 ] ; else dp [ i ] = 1 ; } for ( i = 0 ; i < n ; i ++ ) { c = c + dp [ i ] ; } printf ( "%lld\n" , c ) ; t -- ; } return 0 ; }