int n ; int get_max_sum ( int a [ 100 ] [ 100 ] , int i , int j , int prev ) { int sum = 0 , sum1 , sum2 ; if ( i == n - 1 ) { return prev + a [ i ] [ j ] ; } prev += a [ i ] [ j ] ; sum1 = get_max_sum ( a , i + 1 , j , prev ) ; sum2 = get_max_sum ( a , i + 1 , j + 1 , prev ) ; if ( sum1 > sum2 ) sum = sum1 ; else sum = sum2 ; return sum ; } int main ( ) { int t , arr [ 100 ] [ 100 ] , i , j , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) scanf ( "%d" , & arr [ j ] [ k ] ) ; } printf ( "%d\n" , get_max_sum ( arr , 0 , 0 , 0 ) ) ; } }