int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { unsigned long long int n ; scanf ( "%llu" , & n ) ; unsigned long long int a [ n ] , min , ans , i , j ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%llu" , & a [ i ] ) ; i = 0 ; min = i ; for ( j = i + 1 ; j < n ; j ++ ) if ( a [ j ] < a [ min ] ) min = j ; ans = a [ min ] * ( n - 1 ) ; printf ( "%llu\n" , ans ) ; } return 0 ; }