int ele [ 100 ] , sum [ 100 ] = { 0 } ; int main ( void ) { int n ; int r ; int b ; scanf ( "%d" , & n ) ; scanf ( "%d" , & r ) ; int i , j , z , h ; int max ; for ( h = 0 ; h < n ; h ++ ) { for ( i = 0 ; i < r ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & ele [ j ] ) ; } for ( z = i ; z >= 0 ; z -- ) { if ( z > 0 ) { sum [ z ] = sum [ i - 1 ] + ele [ z ] ; } else { sum [ z ] = sum [ z ] + ele [ z ] ; } } } max = sum [ 0 ] ; for ( b = 0 ; b < r ; b ++ ) { if ( sum [ b ] > max ) { max = sum [ b ] ; } } printf ( "%d\n" , max ) ; } return 0 ; }